import React, { useState } from 'react';
import api from '../api';

const PaymentForm = ({ token, onTransactionUpdate }) => {
  const [receiverEmail, setReceiverEmail] = useState('');
  const [amount, setAmount] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      await api.post(
        '/api/payments',
        { receiverEmail, amount },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage('Payment created successfully and is pending approval.');
      setReceiverEmail('');
      setAmount('');
      onTransactionUpdate(); // Update balance and transaction history
    } catch (error) {
      setMessage('Failed to create payment');
    }
  };

  return (
    <div className="p-5 bg-white rounded-lg shadow-md">
      <h3 className="text-2xl font-semibold mb-4">Make a Payment</h3>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="email"
          placeholder="Receiver's Email"
          value={receiverEmail}
          onChange={(e) => setReceiverEmail(e.target.value)}
          className="input"
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="input"
          required
        />
        <button type="submit" className="btn-yellow">Submit Payment</button>
      </form>
    </div>
  );
};

export default PaymentForm;
