import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import UserHome from './components/UserHome';
import PaymentForm from './components/PaymentForm';
import PaymentHistory from './components/PaymentHistory';
import AdminDashboard from './components/AdminDashboard';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [token, setToken] = useState(null);
  const [role, setRole] = useState(null); // 'user' or 'admin'

  const handleLogin = (userToken, userRole) => {
    setToken(userToken);
    setRole(userRole);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setToken(null);
    setRole(null);
  };

  return (
    <Router>
      <div className="App">
        <h1 className="text-3xl font-bold">Customer Portal</h1>

        <Routes>
          {/* Redirect to dashboard if already authenticated */}
          <Route
            path="/login"
            element={isAuthenticated ? <Navigate to="/" /> : <Login onLogin={handleLogin} />}
          />
          <Route
            path="/register"
            element={isAuthenticated ? <Navigate to="/" /> : <Register onRegister={handleLogin} />}
          />

          {/* Routes for Admin */}
          {isAuthenticated && role === 'admin' && (
            <Route path="/" element={<AdminDashboard token={token} />} />
          )}

          {/* Routes for User */}
          {isAuthenticated && role === 'user' && (
            <>
              <Route path="/" element={<UserHome />} />
              <Route path="/make-payment" element={<PaymentForm token={token} />} />
              <Route path="/payment-history" element={<PaymentHistory token={token} />} />
            </>
          )}

          {/* Redirect any undefined routes */}
          <Route path="*" element={<Navigate to={isAuthenticated ? "/" : "/login"} />} />
        </Routes>

        {/* Logout Button */}
        {isAuthenticated && (
          <button onClick={handleLogout} className="text-blue-500 underline mt-4">
            Logout
          </button>
        )}
      </div>
    </Router>
  );
}

export default App;
