import React, { useState } from 'react';
import api from '../api';

const PaymentForm = ({ token, onTransactionUpdate }) => {
  const [recipientEmail, setRecipientEmail] = useState('');
  const [swiftCode, setSwiftCode] = useState('');
  const [amount, setAmount] = useState('');
  const [currency, setCurrency] = useState('USD');
  const [convertedAmount, setConvertedAmount] = useState(null);
  const [message, setMessage] = useState('');

  // Function to fetch exchange rates from Open Exchange Rates
  const fetchExchangeRate = async (currency) => {
    try {
      const response = await fetch(
        `https://openexchangerates.org/api/latest.json?app_id=${process.env.REACT_APP_OPEN_EXCHANGE_RATES_API_KEY}&symbols=ZAR,${currency}`
      );
      
      if (!response.ok) {
        throw new Error(`Failed to fetch exchange rate: ${response.statusText}`);
      }
  
      const data = await response.json();
  
      // Check if the rates exist
      if (!data.rates || !data.rates.ZAR || !data.rates[currency]) {
        throw new Error('Exchange rates for ZAR or selected currency not available');
      }
  
      // Calculate the exchange rate to ZAR
      return data.rates.ZAR / data.rates[currency];
    } catch (error) {
      console.error('Error fetching exchange rate:', error);
      setMessage('Failed to fetch exchange rate. Please try again later.');
      return null;
    }
  };
  

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setConvertedAmount(null);

    // Convert the amount to ZAR
    const exchangeRate = await fetchExchangeRate(currency);
    if (exchangeRate) {
      const amountInZAR = (amount * exchangeRate).toFixed(2);
      setConvertedAmount(amountInZAR);

      try {
        // Submit payment data to the API
        await api.post(
          '/api/payments',
          {
            recipientEmail,
            swiftCode,
            amount,
            currency,
            amountInZAR, // Send the converted amount to the backend
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setMessage('Funds will go through once approved by the admin.');
        setRecipientEmail('');
        setSwiftCode('');
        setAmount('');
        setCurrency('USD');
        setConvertedAmount(null);
        onTransactionUpdate();
      } catch (error) {
        setMessage('Failed to create payment');
      }
    }
  };

  return (
    <div className="bg-white p-6 rounded-xl shadow-lg max-w-md mx-auto text-center">
      <h2 className="text-2xl font-bold text-yellow-700 mb-6">Send Funds</h2>

      {message && (
        <p className="mb-4 text-sm font-semibold text-green-600 bg-green-100 p-2 rounded-lg">
          {message}
        </p>
      )}

      {convertedAmount && (
        <p className="text-sm font-semibold text-blue-600 bg-blue-100 p-2 rounded-lg mb-4">
          Amount in ZAR: R{convertedAmount}
        </p>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="space-y-2">
          <label htmlFor="recipientEmail" className="block text-left font-semibold text-gray-700">
            Recipient Email
          </label>
          <input
            type="email"
            id="recipientEmail"
            placeholder="Enter recipient's email address"
            value={recipientEmail}
            onChange={(e) => setRecipientEmail(e.target.value)}
            required
            className="w-full p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all"
          />
        </div>

        <div className="space-y-2">
          <label htmlFor="swiftCode" className="block text-left font-semibold text-gray-700">
            SWIFT Code
          </label>
          <input
            type="text"
            id="swiftCode"
            placeholder="Enter SWIFT code"
            value={swiftCode}
            onChange={(e) => setSwiftCode(e.target.value)}
            required
            className="w-full p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all"
          />
        </div>

        <div className="space-y-2">
          <label htmlFor="amount" className="block text-left font-semibold text-gray-700">
            Amount
          </label>
          <input
            type="number"
            id="amount"
            placeholder="Enter amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
            className="w-full p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all"
          />
        </div>

        <div className="space-y-2">
          <label htmlFor="currency" className="block text-left font-semibold text-gray-700">
            Currency
          </label>
          <select
            id="currency"
            value={currency}
            onChange={(e) => setCurrency(e.target.value)}
            className="w-full p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all"
          >
            <option value="USD">USD - US Dollar</option>
            <option value="EUR">EUR - Euro</option>
            <option value="GBP">GBP - British Pound</option>
            <option value="ZAR">ZAR - South African Rand</option>
            {/* Add more currencies as needed */}
          </select>
        </div>

        <button
          type="submit"
          className="w-full py-3 mt-4 bg-gradient-to-r from-yellow-500 to-yellow-600 text-white font-bold rounded-lg shadow-lg hover:from-yellow-600 hover:to-yellow-700 transform transition-all duration-200 hover:scale-105 focus:outline-none focus:ring-2 focus:ring-yellow-400 focus:ring-offset-2"
        >
          Send Payment
        </button>
      </form>
    </div>
  );
};

export default PaymentForm;
