// PaymentForm.js
import React, { useState } from 'react';
import api from '../api';

const PaymentForm = ({ token, onTransactionUpdate }) => {
  const [recipientEmail, setRecipientEmail] = useState('');
  const [swiftCode, setSwiftCode] = useState(''); // Add SWIFT Code input
  const [amount, setAmount] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(''); // Reset message on new submit

    try {
      await api.post('/api/payments', 
        { recipientEmail, swiftCode, amount }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage('Funds will go through once approved by the admin.');
      setRecipientEmail('');
      setSwiftCode(''); // Clear SWIFT code input after submission
      setAmount('');
      onTransactionUpdate();
    } catch (error) {
      setMessage('Failed to create payment');
    }
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Send Funds</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Recipient Email"
          value={recipientEmail}
          onChange={(e) => setRecipientEmail(e.target.value)}
          required
          className="input-field"
        />
        <input
          type="text"
          placeholder="SWIFT Code"
          value={swiftCode}
          onChange={(e) => setSwiftCode(e.target.value)}
          required
          className="input-field"
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
          className="input-field"
        />
        <button type="submit" className="btn-yellow">Send</button>
      </form>
    </div>
  );
};

export default PaymentForm;
