import React, { useState } from 'react';
import { createPayment } from '../api';

const PaymentForm = ({ token }) => {
  const [accountNumber, setAccountNumber] = useState('');
  const [swiftCode, setSwiftCode] = useState('');
  const [amount, setAmount] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const paymentData = { accountNumber, swiftCode, amount };
      await createPayment(token, paymentData);
      setMessage('Payment created successfully!');
      setAccountNumber('');
      setSwiftCode('');
      setAmount('');
    } catch (error) {
      setMessage('Failed to create payment');
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg max-w-md mx-auto mt-10">
      <h2 className="text-3xl font-bold text-center text-yellow-600 mb-6">Make a Payment</h2>
      {message && <p className="text-center text-green-500 mb-4">{message}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Account Number"
          value={accountNumber}
          onChange={(e) => setAccountNumber(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
          required
        />
        <input
          type="text"
          placeholder="SWIFT Code"
          value={swiftCode}
          onChange={(e) => setSwiftCode(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
          required
        />
        <button type="submit" className="w-full p-3 bg-yellow-500 text-black rounded-lg hover:bg-yellow-600 transition duration-300">
          Submit Payment
        </button>
      </form>
    </div>
  );
};

export default PaymentForm;
