import React, { useEffect, useState } from 'react';
import { getPaymentHistory } from '../api';

const PaymentHistory = ({ token }) => {
  const [payments, setPayments] = useState([]);

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const response = await getPaymentHistory(token);
        setPayments(response.data);
      } catch (error) {
        console.error('Failed to fetch payment history');
      }
    };
    fetchPayments();
  }, [token]);

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg max-w-2xl mx-auto mt-10">
      <h2 className="text-3xl font-bold text-center text-yellow-600 mb-6">Payment History</h2>
      <ul className="space-y-4">
        {payments.map((payment) => (
          <li key={payment._id} className="p-4 bg-gray-100 rounded-lg">
            <p className="text-lg text-gray-700">{payment.accountNumber} - {payment.swiftCode} - {payment.amount} - {payment.status}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PaymentHistory;
