import React, { useEffect, useState } from 'react';
import { getPendingPayments, approvePayment, rejectPayment } from '../api';

const AdminDashboard = ({ token }) => {
  const [payments, setPayments] = useState([]);

  useEffect(() => {
    const fetchPendingPayments = async () => {
      try {
        const response = await getPendingPayments(token);
        setPayments(response.data);
      } catch (error) {
        console.error('Failed to fetch pending payments');
      }
    };
    fetchPendingPayments();
  }, [token]);

  const handleApprove = async (id) => {
    try {
      await approvePayment(token, id);
      setPayments(payments.filter(payment => payment._id !== id));
    } catch (error) {
      console.error('Failed to approve payment');
    }
  };

  const handleReject = async (id) => {
    try {
      await rejectPayment(token, id);
      setPayments(payments.filter(payment => payment._id !== id));
    } catch (error) {
      console.error('Failed to reject payment');
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg max-w-2xl mx-auto mt-10">
      <h2 className="text-3xl font-bold text-center text-yellow-600 mb-6">Pending Payments</h2>
      <ul className="space-y-4">
        {payments.map((payment) => (
          <li key={payment._id} className="flex justify-between items-center p-4 bg-gray-100 rounded-lg">
            <div>
              <p className="text-lg font-medium text-gray-700">{payment.accountNumber} - {payment.swiftCode}</p>
              <p className="text-sm text-gray-500">Amount: {payment.amount}</p>
            </div>
            <div className="space-x-2">
              <button onClick={() => handleApprove(payment._id)} className="bg-yellow-500 text-black px-3 py-1 rounded hover:bg-yellow-600">Approve</button>
              <button onClick={() => handleReject(payment._id)} className="bg-gray-500 text-white px-3 py-1 rounded hover:bg-gray-600">Reject</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
