// src/components/Home.js
import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-300 via-yellow-100 to-yellow-200 flex flex-col items-center justify-center p-4 relative">
      {/* Background Decorative Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-10 -right-20 w-96 h-96 bg-yellow-300 rounded-full opacity-20 blur-3xl"></div>
        <div className="absolute -bottom-10 -left-20 w-96 h-96 bg-yellow-500 rounded-full opacity-20 blur-3xl"></div>
      </div>

      {/* Welcome Section */}
      <div className="relative z-10 bg-white/80 backdrop-blur-lg p-10 rounded-2xl shadow-2xl text-center max-w-md mx-auto transform transition duration-500 hover:scale-105">
        <h1 className="text-4xl font-extrabold text-yellow-700 mb-4">
          Welcome to Our International Customer Portal
        </h1>
        <p className="text-lg text-gray-600 mb-8">
          Manage your payments, view statements, and gain financial insights seamlessly. Your gateway to secure international financial management.
        </p>
        <Link
          to="/login"
          className="inline-block bg-gradient-to-r from-yellow-500 to-yellow-600 text-white font-semibold py-3 px-8 rounded-full shadow-lg transform transition duration-300 hover:scale-105 hover:from-yellow-600 hover:to-yellow-700"
        >
          Login to Your Account
        </Link>
      </div>

      {/* Credits Section */}
      <footer className="mt-10 text-gray-600 text-sm relative z-10 text-center">
        Made with ❤️ by <span className="font-semibold">Tamir, Rohan, Thiyan, Raesa, and Eshvara</span>
      </footer>
    </div>
  );
};

export default Home;
