// src/components/FinancialInsights.js

import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

const FinancialInsights = ({ transactions = [] }) => {
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    if (transactions.length === 0) {
      // Handle the case where transactions are empty
      setChartData({
        labels: [],
        datasets: [
          {
            label: 'No data available',
            data: [],
            borderColor: '#ff6384',
          },
        ],
      });
      return;
    }

    // Process the transactions to prepare the data for the chart
    const dates = transactions.map(tx => new Date(tx.date).toLocaleDateString());
    const spendingAmounts = transactions.map(tx => (tx.amount < 0 ? Math.abs(tx.amount) : 0));

    setChartData({
      labels: dates,
      datasets: [
        {
          label: 'Spending Over Time',
          data: spendingAmounts,
          fill: false,
          borderColor: '#ff6384',
          tension: 0.1,
        },
      ],
    });
  }, [transactions]);

  const options = {
    responsive: true,
    plugins: {
      legend: {
        display: true,
        position: 'top',
      },
      tooltip: {
        enabled: true,
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Date',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Amount (R)',
        },
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow-lg">
      <h2 className="text-xl font-semibold text-center mb-4 text-yellow-600">Financial Insights</h2>
      {transactions.length > 0 ? (
        <Line data={chartData} options={options} />
      ) : (
        <p className="text-center text-gray-500">No transaction data available to display.</p>
      )}
    </div>
  );
};

export default FinancialInsights;
