import React, { useState } from 'react';
import Login from './components/Login';
import Register from './components/Register';
import PaymentForm from './components/PaymentForm';
import PaymentHistory from './components/PaymentHistory';
import AdminDashboard from './components/AdminDashboard';

function App() {
  const [isRegistering, setIsRegistering] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [token, setToken] = useState(null);
  const [role, setRole] = useState(null); // 'user' or 'admin'

  const toggleForm = () => {
    setIsRegistering(!isRegistering);
  };

  const handleLogin = (userToken, userRole) => {
    setToken(userToken);
    setRole(userRole);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setToken(null);
    setRole(null);
  };

  return (
    <div className="App">
      <h1 className="text-3xl font-bold">Customer Portal</h1>

      {!isAuthenticated ? (
        isRegistering ? (
          <>
            <Register onRegister={handleLogin} />
            <p>
              Already have an account?{' '}
              <button onClick={toggleForm} className="text-blue-500 underline">
                Login
              </button>
            </p>
          </>
        ) : (
          <>
            <Login onLogin={handleLogin} />
            <p>
              Don't have an account?{' '}
              <button onClick={toggleForm} className="text-blue-500 underline">
                Register
              </button>
            </p>
          </>
        )
      ) : role === 'admin' ? (
        <>
          <AdminDashboard token={token} />
          <button onClick={handleLogout} className="text-blue-500 underline mt-4">
            Logout
          </button>
        </>
      ) : (
        <>
          <PaymentForm token={token} />
          <PaymentHistory token={token} />
          <button onClick={handleLogout} className="text-blue-500 underline mt-4">
            Logout
          </button>
        </>
      )}
    </div>
  );
}

export default App;
