import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import UserHome from './components/UserHome';
import AdminDashboard from './components/AdminDashboard';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [token, setToken] = useState(null);
  const [role, setRole] = useState(null); // 'user' or 'admin'

  const handleLogin = (userToken, userRole) => {
    setToken(userToken);
    setRole(userRole);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setToken(null);
    setRole(null);
  };

  useEffect(() => {
    console.log("Authenticated:", isAuthenticated);
    console.log("Role:", role);
    console.log("Token:", token);
  }, [isAuthenticated, role, token]);

  return (
    <Router>
      <div className="App">
        <h1 className="text-3xl font-bold text-center mb-6">Customer Portal</h1>

        <Routes>
          {/* Public Routes */}
          <Route
            path="/login"
            element={isAuthenticated ? <Navigate to="/" /> : <Login onLogin={handleLogin} />}
          />
          <Route
            path="/register"
            element={isAuthenticated ? <Navigate to="/" /> : <Register onRegister={handleLogin} />}
          />

          {/* Admin Route */}
          {isAuthenticated && role === 'admin' && (
            <Route path="/" element={<AdminDashboard token={token} />} />
          )}

          {/* User Route */}
          {isAuthenticated && role === 'user' && (
            <Route path="/" element={<UserHome token={token} />} />
          )}

          {/* Fallback: Redirect to login if not authenticated */}
          <Route path="/" element={isAuthenticated ? (role === 'admin' ? <AdminDashboard token={token} /> : <UserHome token={token} />) : <Navigate to="/login" />} />
          </Routes>

        {/* Logout Button */}
        {isAuthenticated && (
          <div className="text-center mt-4">
            <button
              onClick={handleLogout}
              className="p-2 bg-yellow-500 text-black rounded hover:bg-yellow-600 transition duration-300"
            >
              Logout
            </button>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
