import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import PaymentForm from './PaymentForm';
import TransferFunds from './TransferFunds';
import RecentTransactions from './RecentTransactions';
import FinancialInsights from './FinancialInsights';
import Statements from './Statements';
import api from '../api';


const UserHome = ({ token }) => {
  const [balance, setBalance] = useState(10000); // Default balance
  const [transactions, setTransactions] = useState([]);
  const [selectedTab, setSelectedTab] = useState('overview');

  useEffect(() => {
    // Load user's balance and transactions from API
    fetchUserBalanceAndTransactions();
  }, []);

  const fetchUserBalanceAndTransactions = async () => {
    try {
      // Fetch initial balance and transaction history from backend
      const response = await api.get('/api/user/balance', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setBalance(response.data.balance);
      setTransactions(response.data.transactions);
    } catch (error) {
      console.error('Failed to fetch balance and transactions');
    }
  };

  return (
    <div className="max-w-4xl mx-auto mt-10 p-5 bg-white rounded-lg shadow-lg">
      <h2 className="text-3xl font-bold text-yellow-600 text-center mb-6">Welcome to Your Dashboard</h2>

      {/* Account Overview */}
      {selectedTab === 'overview' && (
        <section className="bg-gray-100 p-5 rounded-lg mb-6">
          <h3 className="text-xl font-semibold text-gray-700 mb-3">Account Overview</h3>
          <div className="flex justify-between items-center">
            <div>
              <p className="text-lg text-gray-700">Account Balance:</p>
              <p className="text-2xl font-bold text-yellow-600">R{balance.toFixed(2)}</p>
            </div>
          </div>
        </section>
      )}

      {/* Tabs for each feature */}
      <div className="mb-6">
        <button onClick={() => setSelectedTab('makePayment')} className="btn-yellow">Make a Payment</button>
        <button onClick={() => setSelectedTab('transferFunds')} className="btn-yellow">Transfer Funds</button>
        <button onClick={() => setSelectedTab('recentTransactions')} className="btn-yellow">Recent Transactions</button>
        <button onClick={() => setSelectedTab('financialInsights')} className="btn-yellow">Financial Insights</button>
        <button onClick={() => setSelectedTab('statements')} className="btn-yellow">View Statements</button>
      </div>

      {/* Conditional Rendering based on Tab Selection */}
      {selectedTab === 'makePayment' && <PaymentForm token={token} onTransactionUpdate={fetchUserBalanceAndTransactions} />}
      {selectedTab === 'transferFunds' && <TransferFunds balance={balance} token={token} onTransactionUpdate={fetchUserBalanceAndTransactions} />}
      {selectedTab === 'recentTransactions' && <RecentTransactions transactions={transactions} />}
      {selectedTab === 'financialInsights' && <FinancialInsights transactions={transactions} />}
      {selectedTab === 'statements' && <Statements transactions={transactions} />}
    </div>
  );
};

export default UserHome;
