// src/components/Home.js
import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-100 via-yellow-200 to-yellow-100 flex flex-col items-center justify-center p-4">
      {/* Welcome Section */}
      <div className="bg-white/90 p-8 rounded-lg shadow-lg text-center max-w-lg">
        <h1 className="text-4xl font-bold text-yellow-700 mb-4">Welcome to Our International Customer Portal</h1>
        <p className="text-lg text-gray-700 mb-8">
          Manage your payments, view statements, and gain financial insights seamlessly.
        </p>
        <Link
          to="/login"
          className="inline-block bg-yellow-500 text-white py-3 px-6 rounded-lg shadow-md hover:bg-yellow-600 transition-all duration-200"
        >
          Login to Your Account
        </Link>
      </div>

      {/* Credits */}
      <footer className="mt-10 text-gray-600 text-sm">
        Made with ❤️ by Tamir, Thiyan, Raesa, and Eshvara
      </footer>
    </div>
  );
};

export default Home;
