import React, { useEffect, useState } from 'react';
import { CheckCircle, XCircle, Loader } from 'lucide-react';
import { getPendingPayments, approvePayment, rejectPayment } from '../api';

const AdminDashboard = ({ token }) => {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPendingPayments = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await getPendingPayments(token);
        setPayments(response.data);
      } catch (error) {
        console.error('Failed to fetch pending payments');
        setError('Failed to fetch pending payments. Please try again.');
      } finally {
        setLoading(false);
      }
    };
    fetchPendingPayments();
  }, [token]);

  const handleApprove = async (id) => {
    setError(null);
    setMessage(null);
    try {
      await approvePayment(token, id);
      setPayments(payments.filter(payment => payment._id !== id));
      setMessage('Payment approved successfully.');
    } catch (error) {
      console.error('Failed to approve payment');
      setError('Failed to approve payment. Please try again.');
    }
  };

  const handleReject = async (id) => {
    setError(null);
    setMessage(null);
    try {
      await rejectPayment(token, id);
      setPayments(payments.filter(payment => payment._id !== id));
      setMessage('Payment rejected successfully.');
    } catch (error) {
      console.error('Failed to reject payment');
      setError('Failed to reject payment. Please try again.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200 p-8">
      <div className="max-w-4xl mx-auto bg-white rounded-2xl shadow-lg">
        <div className="bg-gradient-to-r from-yellow-500 to-yellow-600 p-6 rounded-t-2xl">
          <h2 className="text-3xl font-bold text-white text-center">Admin Dashboard</h2>
          <p className="text-white text-center">Manage Pending Payments</p>
        </div>

        {/* Feedback Messages */}
        {message && (
          <div className="bg-green-50 border-l-4 border-green-500 p-4 text-green-700 text-center">
            {message}
          </div>
        )}
        {error && (
          <div className="bg-red-50 border-l-4 border-red-500 p-4 text-red-700 text-center">
            {error}
          </div>
        )}

        {loading ? (
          <div className="p-10 flex items-center justify-center">
            <Loader className="animate-spin text-yellow-500 w-10 h-10" />
          </div>
        ) : (
          <ul className="p-6 space-y-4">
            {payments.length === 0 ? (
              <p className="text-gray-500 text-center">No pending payments at the moment.</p>
            ) : (
              payments.map((payment) => (
                <li key={payment._id} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg shadow-md">
                  <div>
                    <p className="text-lg font-medium text-gray-700">{payment.accountNumber} - {payment.swiftCode}</p>
                    <p className="text-sm text-gray-500">Amount: R{payment.amount}</p>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleApprove(payment._id)}
                      className="flex items-center bg-green-500 text-white px-3 py-1 rounded-lg hover:bg-green-600 transition-all"
                    >
                      <CheckCircle size={18} className="mr-1" />
                      Approve
                    </button>
                    <button
                      onClick={() => handleReject(payment._id)}
                      className="flex items-center bg-red-500 text-white px-3 py-1 rounded-lg hover:bg-red-600 transition-all"
                    >
                      <XCircle size={18} className="mr-1" />
                      Reject
                    </button>
                  </div>
                </li>
              ))
            )}
          </ul>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
