import React, { useState } from 'react';
import api from '../api';

const TransferFunds = ({ balance, token, onTransactionUpdate }) => {
  const [amount, setAmount] = useState('');
  const [message, setMessage] = useState('');

  const handleTransfer = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      await api.post(
        '/api/user/transfer',
        { amount },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage('Transfer successful');
      setAmount('');
      onTransactionUpdate(); // Update balance and transaction history
    } catch (error) {
      setMessage('Failed to transfer funds');
    }
  };

  return (
    <div className="p-5 bg-white rounded-lg shadow-md">
      <h3 className="text-2xl font-semibold mb-4">Transfer Funds</h3>
      {message && <p>{message}</p>}
      <form onSubmit={handleTransfer} className="space-y-4">
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="input"
          required
        />
        <button type="submit" className="btn-yellow">Transfer</button>
      </form>
    </div>
  );
};

export default TransferFunds;
